commits.content:
@charset "UTF-8";

.content-section {
  position: relative; /*min-width:1842px;*/
  background: #f7f8fb;
  min-height: 100vh;
  top: 0;
  left: 80px;
  width: calc(100% - 80px);
  z-index: 2;
}
.content-section .title {
  display: inline-block;
  color: #41495e;
  line-height: 32px;
  font-size: 22px;
  font-weight: 700;
}
.content-section .title .tx-org {
  font-size: 18px;
  line-height: 26px;
}
.content-section .title + .filter-box {
  margin-top: 30px;
}
.content-section .header {
  height: 70px;
  padding: 0 30px;
}

.content-section.setting {
  padding-left: 200px;
  min-width: 1600px;
  width: calc(100% - 280px);
}
.setting-nav {
  position: absolute;
  top: 0;
  left: 0;
  width: 200px;
  height: 100%;
  background-color: #fff;
  box-shadow: 0px 8px 10px rgba(0, 0, 0, 0.16);
  box-sizing: border-box;
}
.setting-nav .inbox {
  padding: 23px 16px;
}
.setting-nav .inp-title {
  margin-bottom: 24px;
}
.setting-nav .menu-name {
  padding-left: 22px;
  background-position: left center;
  background-repeat: no-repeat;
  font-weight: bold;
  font-size: 15px;
  line-height: 22px;
  color: #41495d;
}
.setting-nav .menu-name.company {
  background-image: url(../images/setting/ico_setting-company.png);
}
.setting-nav .menu-name.security {
  background-image: url(../images/setting/ico_setting-security.png);
}
.setting-nav .menu-name.layout {
  background-image: url(../images/setting/ico_setting-layout.png);
}
.setting-nav .menu-name.card {
  background-image: url(../images/setting/ico_setting-card.png);
}
.setting-nav .menu-name.chat {
  background-image: url(../images/setting/ico_setting-chat.png);
}
.setting-nav .depth2 {
  margin: 10px 0 12px;
  padding-left: 14px;
}
.setting-nav .depth2 > li a {
  display: block;
  width: 100%;
  padding: 6px 8px;
  border-radius: 4px;
  box-sizing: border-box;
  font-size: 14px;
  line-height: 20px;
  color: #555868;
}
.setting-nav .depth2 > li.on a {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: rgba(29, 88, 255, 0.16);
  font-weight: bold;
  color: #41495d;
}
.setting-nav .depth2 > li.warn a {
  padding-right: 18px;
  background: url(../images/setting/ico_setting-warning.png) no-repeat right
    center;
}
.setting-nav .depth2 > li .tx-blue {
  font-size: 11px;
  line-height: 16px;
  font-weight: 500;
}
/* dashboard */
.dashboard {
  position: relative;
  min-height: 913px;
  height: 100vh;
  top: 0;
  left: 80px;
  width: calc(100% - 80px);
  z-index: 2;
  background-color: #f7f8fb;
}
.dashboard .header {
  padding: 14px 0;
  justify-content: flex-end;
}

.main-box-wrap {
  margin-top: 30px;
  height: calc(100% - 102px);
}
.main-box-wrap .cnt-box {
  padding: 35px 26px;
  width: 17%;
  min-width: 300px;
  height: 246px;
  border-radius: 16px;
  box-shadow: 1px 1px 30px rgba(29, 88, 255, 0.06);
  background-repeat: no-repeat;
  background-position: top 35px left 26px;
}
.main-box-wrap .cnt-box + .cnt-box,
.main-box-wrap .btm-cnt-box + .btm-cnt-box {
  margin-left: 22px;
}
.main-box-wrap .cnt-box.customer {
  background-image: url(../images/main/img_main-customer.png);
}
.main-box-wrap .cnt-box.issue {
  background-image: url(../images/main/img_main-issue.png);
}
.main-box-wrap .cnt-box.estimate {
  background-image: url(../images/main/img_main-estimate.png);
}
.main-box-wrap .cnt-box.alarm {
  background-image: url(../images/main/img_main-alarm.png);
}
.main-box-wrap .cnt-box .info-cnt {
  padding-top: 85px;
}
.main-box-wrap .cnt-box .profile-name {
  margin-bottom: 12px;
  padding-right: 25px;
  background: url(../images/main/ico_main-arrow.png) no-repeat right center;
  font-weight: bold;
  font-size: 16px;
  line-height: 23px;
  color: #7c8094;
}
.main-box-wrap .cnt-box .num {
  display: inline-block;
  padding-right: 2%;
  max-width: 90%;
  height: 55px;
  overflow: hidden;
  font-style: italic;
  font-weight: bold;
  font-size: 46px;
  line-height: 55px;
  color: #4e4b5c;
}
.main-box-wrap .cnt-box .unit {
  display: inline-block;
  font-weight: 500;
  font-size: 16px;
  line-height: 23px;
  color: #4e4b5c;
  vertical-align: 7px;
}

.main-box-wrap .cnt-box.wide {
  width: 27%;
  padding-bottom: 30px;
}
.main-box-wrap .cnt-box.wide .profile-head {
  position: relative;
  justify-content: center;
  min-height: 135px;
  border-bottom: 1px solid #e1e1e1;
  text-align: center;
  box-sizing: border-box;
}
.main-box-wrap .cnt-box.wide .profile-head .profile {
  display: block;
  margin: 0 auto;
  width: 70px;
  height: 70px;
  margin-bottom: 14px;
  background: url(../images/common/bg_user.png) no-repeat center/100% auto;
}
.main-box-wrap .cnt-box.wide .profile-info {
  padding: 20px 16px 0;
}
.main-box-wrap .cnt-box.wide .profile-info .name {
  font-weight: bold;
  font-size: 18px;
  line-height: 26px;
  color: #414c5d;
}
.btn-code-confirm {
  padding-left: 22px;
  background: url(../images/main/img_main-code.png) no-repeat left center;
  font-size: 16px;
  line-height: 23px;
  text-align: center;
  text-decoration-line: underline;
  color: #9a9a9a;
}
.btn-code-confirm.on {
  background-image: url(../images/main/img_main-code-hover.png);
  font-weight: 500;
  color: #0f3293;
}

.dashboard {
  padding: 0 30px;
  min-width: 1740px;
  box-sizing: border-box;
}
.dashboard .btm-cnt-box .cnt-box {
  position: relative;
  padding: 35px 36px;
  height: calc(100% - 56px);
}
.dashboard .profile-head .alert {
  padding-left: 30px;
  background: url(../images/main/ico_main-alert.png) no-repeat left center;
  font-weight: bold;
  font-size: 15px;
  line-height: 22px;
  color: #ff8159;
}
.dashboard .profile-head .pos.top {
  top: 0;
  left: 0;
  transform: none;
  right: auto;
}
.flex-colbox {
  display: flex;
  flex-direction: column;
}
.dashboard .col-wrap.approval .cnt-box {
  padding-bottom: 32px;
  height: 118px;
  min-height: 118px;
}
.dashboard .col-wrap.approval .cnt-box.flex-sidebox > p {
  display: flex;
  align-items: flex-end;
  width: 327px;
}
.dashboard .col-wrap.approval .cnt-box .unit {
  margin-bottom: 3px;
}
.dashboard .col-wrap.approval .cnt-box .btn {
  min-width: 119px;
}
/* .dashboard .col-wrap.history .cnt-box{height:100%  } */
/* .dashboard .col-wrap.history .cnt-box{height:402px; } */
.btm-cnt-box.circle {
  width: 17%;
}
.btm-cnt-box.circle .cnt-box,
.dashboard .btm-cnt-box.normal .cnt-box {
  min-height: 453px;
}
.dashboard .btm-cnt-box.normal {
  width: 48.5%;
}
.dashboard .btm-cnt-box.circle .cnt-box,
.dashboard .btm-cnt-box.normal .cnt-box {
  width: 100%;
}
.dashboard .main-btm-box {
  width: 100%;
  height: calc(100vh - 428px);
}
.main-top-box {
  margin-bottom: 40px;
}
.dashboard .col-wrap + .col-wrap {
  margin-top: 30px;
  height: 100%;
  overflow: hidden;
}
.btm-cnt-box .col-wrap .cnt-box {
  width: 100%;
}

.btm-cnt-box.flex-colbox {
  width: 32%;
  height: 100%;
}

.board-cnt-title {
  margin-bottom: 26px;
  font-weight: bold;
  font-size: 18px;
  line-height: 26px;
  color: #414c5d;
}
.btn-blue {
  padding: 11px 30px;
  width: 119px;
  height: 45px;
  background: #1d58ff;
  border-radius: 6px;
  font-weight: bold;
  font-size: 16px;
  line-height: 23px;
  text-align: center;
  color: #ffffff;
}
.chart-circle-wrap {
  /* margin:25% 0 10px; */
  width: 100%;
  padding-bottom: 42px;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
}
.main-box-wrap .cnt-box .num02 {
  display: block;
  position: absolute;
  left: 50%;
  top: 73%;
  transform: translate(-50%, -50%);
  text-align: center;
}
.main-box-wrap .cnt-box .num02 {
  font-size: 16px;
  line-height: 23px;
  color: #41495d;
}
.main-box-wrap .cnt-box .num02 strong {
  font-weight: bold;
  color: #41495d;
}
.main-box-wrap .cnt-box .num02 strong var {
  font-size: 22px;
  line-height: 32px;
}

.custom-legend .dot {
  display: inline-block;
  margin-right: 6px;
  width: 14px;
  height: 14px;
  border-radius: 3px;
  background: #1d58ff;
  vertical-align: -2px;
}
.custom-legend li {
  position: relative;
}
.custom-legend.type01 li + li {
  margin-top: 10px;
}
.custom-legend.type02 {
  display: flex;
}
.custom-legend.type02 li + li {
  margin-left: 22px;
}
.custom-legend input {
  position: absolute;
  left: 0;
  top: 0;
  min-width: 1em;
  width: 100%;
  height: 100%;
  z-index: 2;
  opacity: 0;
  margin: 0;
  padding: 0;
  cursor: pointer;
}

.btm-cnt-box .cnt-box .custom-legend {
  position: absolute;
  z-index: 1;
  font-size: 16px;
  color: #414c5d;
}

/*.btm-cnt-box.circle .cnt-box {min-height:602px;}*/
.btm-cnt-box.circle .cnt-box .custom-legend {
  left: 36px;
  bottom: 34px;
}
/* .btm-cnt-box.normal .cnt-box {min-height:602px;} */
.btm-cnt-box.normal .cnt-box {
  display: flex;
  align-items: flex-end;
}
.btm-cnt-box.normal .cnt-box .custom-legend {
  left: 36px;
  top: 35px;
}
.btm-cnt-box.normal .chart-wrap {
  width: 100%;
} /* margin-top:62px*/
.btm-cnt-box .col-wrap.history {
  height: calc(100% - 52px);
  min-height: 306px;
}
.btm-cnt-box .col-wrap.history .inbox {
  overflow: auto;
  height: 100%;
}
.btm-cnt-box .col-wrap.history .cnt-box {
  padding-bottom: 15px;
}
.btm-cnt-box .col-wrap:first-child:last-child {
  height: 100%;
}
.btn-tbl-remove {
  width: 24px;
  height: 24px;
  background: url(../images/common/ico_tbl-remove.png) no-repeat center;
}
/* normal */
.btm-cnt-box .apexcharts-legend {
  padding: 0;
}
.btm-cnt-box .apexcharts-legend-marker {
  margin-right: 8px;
}
.btm-cnt-box.normal .apexcharts-legend-series + .apexcharts-legend-series {
  margin-left: 22px !important;
}
/* //dashboard */
.inp-title {
  font-weight: bold;
  font-size: 16px;
  line-height: 23px;
}
.inp-title:focus {
  border: 1px solid #8b94ae;
  border-radius: 6px;
}
.inp-title::placeholder {
  font-weight: 400 !important;
  color: rgba(255, 255, 255, 0.7) !important;
}
.inp-title::-webkit-input-placeholder {
  font-weight: 400 !important;
  color: rgba(255, 255, 255, 0.7) !important;
}
.inp-title:-ms-input-placeholder {
  font-weight: 400 !important;
  color: rgba(255, 255, 255, 0.7) !important;
}

/* 폼상세 */
.content-section.type02,
.content-section.type03 {
  width: 100%;
  left: 0;
  background: #e5e5e5;
}
.content-section.type02 .header,
.content-section.type03 .header {
  background: #fff;
}
.content-section.type02 .cnt-box-wrap {
  padding: 0;
  min-height: calc(100vh - 130px);
  flex-direction: column;
  justify-content: space-between;
  overflow: hidden;
}
.content-section.type02 .cnt-box-side {
  display: flex;
  justify-content: space-between;
  overflow: hidden;
}

.content-section.type03 .cnt-box-wrap {
  min-height: calc(100vh - 70px);
  box-sizing: border-box;
  flex-direction: column;
  justify-content: space-between;
  overflow: hidden;
}
.content-section.type04 {
  min-width: auto;
}

.tbl-top.band {
  margin-bottom: 0px;
  padding: 8px 30px;
  background: #02164f;
  height: 60px;
  box-sizing: border-box;
}
.tbl-top.band > * {
  flex: 1;
}
.tbl-top.band .inp-title {
  max-width: 280px;
  height: 44px;
  border: none;
  font-size: 16px;
  font-weight: 700;
  color: #fff;
}
.tbl-top.band .inp-title:focus {
  border: 1px solid #8b94ae;
  border-radius: 6px;
}
.tbl-top.band .tab-wrap {
  display: inline-block;
  margin-left: -26px;
}
.tbl-top.band .band-center .tabs li {
  margin: 0 17px;
}

.tbl-top.band .title {
  font-size: 18px;
  line-height: 23px;
  color: #fff;
}
.tbl-top.band .tab-wrap .tabs {
  justify-content: center;
  overflow: hidden;
}
.tbl-top.band .row-list {
