/* switch */
.switch {
  position: relative;
  display: inline-block;
  width: 56px;
  height: 32px;
}
.switch input {
  opacity: 0;
  width: 0;
  height: 0;
}
.switch input:focus {
  outline: none;
}
.switch .slider {
  position: absolute;
  cursor: pointer;
  width: auto;
  height: 100%;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #ccc;
  -webkit-transition: 0.4s;
  transition: 0.4s;
  outline: none;
}
.switch .slider:before {
  position: absolute;
  content: "";
  height: 24px;
  width: 24px;
  left: 4px;
  bottom: 4px;
  background-color: white;
  -webkit-transition: 0.4s;
  transition: 0.4s;
}
.switch .slider:after {
  display: block;
  position: absolute;
  left: 5px;
  top: 50%;
  margin-top: -7px;
  line-height: 14px;
  font-size: 10px;
  color: #929292;
  content: "OFF";
  transition: 1s;
}
.switch input:checked + .slider {
  background-color: #02164f;
}
.switch input:checked + .slider:before {
  -webkit-transform: translateX(24px);
  -ms-transform: translateX(24px);
  transform: translateX(24px);
}
.switch input:checked + .slider:after {
  left: unset;
  right: 10px;
  content: "ON";
}

div.switch.no-txt {
  display: block;
}
.switch.no-txt {
  width: 48px;
  height: 28px;
}
.switch.no-txt .slider {
  position: absolute;
  cursor: pointer;
  width: auto;
  height: 100%;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #ccc;
  -webkit-transition: 0.4s;
  transition: 0.4s;
}
.switch.no-txt .slider:before {
  position: absolute;
  content: "";
  height: 20px;
  width: 20px;
  left: 4px;
  bottom: 4px;
  background-color: white;
  -webkit-transition: 0.4s;
  transition: 0.4s;
}
.switch.no-txt .slider:after {
  content: "";
}
.switch.no-txt input:checked + .slider {
  background-color: #02164f;
}
.switch.no-txt input:focus + .slider {
  box-shadow: 0 0 1px #02164f;
}
.switch.no-txt input:checked + .slider:before {
  -webkit-transform: translateX(20px);
  -ms-transform: translateX(20px);
  transform: translateX(20px);
}
.switch.no-txt input:checked + .slider:after {
  content: "";
}
/* Rounded sliders */
.switch .slider.round {
  border-radius: 34px;
}
.switch .slider.round:before {
  border-radius: 50%;
}

.switch.mode {
  margin: 20px auto 0;
