/*===== chk =====*/
.checkbox {
  position: relative;
}
.checkbox input {
  position: absolute;
  top: 0;
  left: 0;
  width: 1px;
  height: 1px;
  opacity: 0;
  filter: alpha(opacity=0);
  zoom: 1;
  text-indent: -9999px;
  z-index: 0;
  overflow: hidden;
}
.checkbox .box {
  position: relative;
  display: inline-block;
  width: 18px;
  height: 18px;
  border: 1px solid #e2e2e2;
  vertical-align: top;
  margin: 2px 8px 0 0;
  border-radius: 4px;
}
.checkbox .box:before,
.checkbox .box:after {
  display: block;
  position: absolute;
  transform: rotate(45deg);
  content: "";
  background: #ccc;
}
.checkbox .box:before {
  width: 2px;
  height: 11px;
  top: 3px;
  left: 9px;
}
.checkbox .box:after {
  width: 4px;
  height: 2px;
  top: 9px;
  left: 3px;
}
.checkbox input.check:checked + label .box::before,
.checkbox input.check:checked + label .box:after {
  background-color: #fff;
}
.checkbox input.check:checked + label .box {
  background: #02164f;
  border-color: #02164f;
}
.checkbox input.check + label {
  vertical-align: top;
}
.checkbox input.check + label strong {
  vertical-align: middle;
}
.checkbox input.check + label .box.round {
  border-radius: 50%;
}
.checkbox input.check:checked + label .box.round {
  background-color: #fff;
  border-color: #1d58ff;
}
.checkbox input.check:checked + label .box.round:before,
.checkbox input.check:checked + label .box.round:after {
  background-color: #1d58ff;
}

.checkbox.type02 .box {
  background: #aec0e9;
  border-color: #aec0e9;
  border-radius: 50%;
}
.checkbox.type02 .box:before,
.checkbox.type02 .box:after {
  background-color: #fff;
}
.checkbox.type02 input.check:checked + label .box {
  background-color: #fff;
}
.checkbox.type02 input.check:checked + label .box::before,
.checkbox.type02 input.check:checked + label .box:after {
  background-color: #02164f;
}

