  background-color: #1d58ff;
  color: #fff;
}

.list-linkbox {
  margin-top: 15px;
}
.list-linkbox .checkbox {
  vertical-align: middle;
}
.list-linkbox .name {
  margin-right: 6px;
  font-weight: bold;
  font-size: 20px;
  line-height: 29px;
  color: #41495d;
  vertical-align: middle;
}
.list-linkbox .date {
  font-size: 15px;
  line-height: 22px;
  color: #41495d;
  vertical-align: middle;
}
.list-linkbox:not(card) .badge.position {
  margin-left: 10px;
  margin-right: 12px;
}
.list-linkbox > li > a {
  position: relative;
  display: block;
}
.list-linkbox > li > a:after {
  display: block;
  margin-top: -15px;
  position: absolute;
  top: 50%;
  right: 20px;
  width: 30px;
  height: 30px;
  background: url(../images/common/ico_accordion-arrow.png) no-repeat center;
  content: "";
  transform: rotate(-90deg);
  content: "";
}

.btn-down {
  float: right;
  font-weight: 500;
  font-size: 12px;
  line-height: 18px;
  color: #b8b8b8;
}

.tx-darkblue {
  color: #41495d !important;
}
/* .btn-light-blue:disabled {background-color:rgba(225, 229, 239, 0.5) !important; color:#9A9A9A !important} */

.btn-drop-type02.open + .drop-menu-list {
  max-height: 241px;
  overflow: auto;
}

.drop-menu-list {
  transition: all 0.35s ease-in;
}

.dim {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: transparent;
  z-index: 1;
}

/* btn-link-type02 */
.btn-link-type02 .txt {
  position: relative;
  padding-right: 30px;
}
.btn-link-type02 .txt:after {
  display: block;
  margin-top: -14px;
  position: absolute;
  top: 50%;
  right: 0px;
  width: 30px;
  height: 30px;
  background: url(../images/common/ico_accordion-arrow.png) no-repeat center;
  content: "";
  background-size: 60%;
  transform: rotate(-90deg);
  color: #7b7b7b;
  content: "";
}

.tbl-top-type02 {
  margin-bottom: 30px;
  text-align: center;
}
.login-wrap .tab-wrap {
  width: 175px;
  margin: 0 auto 20px;
}
.login-wrap .tab-wrap .tabs {
  border-bottom: none;
  justify-content: center;
}
.login-wrap .tab-wrap .tabs > li {
  text-align: center;
  margin: 0;
  padding: 0 18px;
}
.login-wrap .tab-btn {
  color: #9a9a9a;
}
.login-wrap .tab-wrap .slider {
  border-radius: 7px;
}
.tab-wrap.type02 {
  width: auto;
  margin-top: 40px;
  margin-bottom: 38px;
}
.tab-wrap.type02 .tabs > li {
  width: 50%;
  padding: 0;
  border-bottom: 1px solid #e6e6e6;
}
.tab-wrap.type02 .tabs li button {
  width: 100%; /*padding-bottom:14px*/
}
.tab-wrap.type03 {
  width: auto;
  margin-top: 20px;
  margin-bottom: 20px;
}
.tab-wrap.type03 .tabs > li {
  width: 50%;
}
.tab-wrap.type03 .tabs li button {
  width: 100%;
}
.txt-form {
  margin: 0 10px 0 8px;
}

/* toast */
#toast {
  position: absolute;
  top: 80px;
  left: 0;
  right: 0px;
  margin: 0 auto;
  display: flex;
  border-radius: 5px;
  margin-top: 10px;
  flex-direction: column;
  align-items: center;
  z-index: 1001;
}
.toast {
  width: 706px;
  height: 60px;
  padding: 20px 20px 20px 54px;
  background: #1d58ff url(../images/common/ico_toast.png) no-repeat left 25px
    center;
  box-shadow: 0px 2px 12px rgba(29, 88, 255, 0.51);
  border-radius: 10px;
  user-select: none;
  font-size: 14px;
  line-height: 20px;
  color: #fff;
  box-sizing: border-box;
  transition: all 1s;
  opacity: 0;
}
.toast + .toast {
  margin-top: 20px;
}
.toast.active {
  opacity: 1;
}

.tx-imp:before {
  margin-right: 4px;
  content: "*";
  vertical-align: top;
  color: #ff8159;
}

.left-bar-style,
.right-bar-style {
  height: calc(100vh - 130px);
  background-color: #fff;
}
.left-bar-style {
  width: 300px;
  border-right: 1px solid #e3e3e3;
}
.right-bar-style {
  padding: 18px 26px 30px;
  width: calc(100% - 300px);
  box-sizing: border-box;
}

.left-bar-style .tabs {
  flex-wrap: wrap;
  border-bottom: none;
}
.left-bar-style .tabs li {
  position: relative;
  width: 100%;
  margin-left: 0;
}
.left-bar-style .tabs li + li:before {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  margin: 0 auto;
  width: 240px;
  height: 1px;
  background-color: #e3e3e3;
  content: "";
}
.left-bar-style .tabs li button {
  width: 100%;
  text-align: left;
  padding: 19px 31px 19px 61px;
  font-weight: 400;
  font-size: 16px;
  color: #7b7b7b;
}
.right-bar-style .tab-content02 {
  height: calc(100% - 136px);
}
.right-bar-style .content,
.right-bar-style .manager-box {
  height: 100%;
}
.right-bar-style .cnt-title {
  margin-top: 0;
  font-size: 18px;
  line-height: 26px;
  color: #41495e;
}
.right-bar-style .info {
  font-size: 14px;
  line-height: 20px;
}
.right-bar-style .cnt-title + .info {
  margin-top: 18px;
  margin-bottom: 36px;
}

.left-bar-style .tabs li button:hover,
.left-bar-style .tabs li.on button {
  color: #1d58ff;
  font-weight: 500;
}

