.daterangepicker {
  position: absolute;
  color: inherit;
  background-color: #fff;
  box-sizing: border-box;
  box-shadow: 1px 4px 8px rgba(0, 0, 0, 0.12);
  border-radius: 6px;
  border: 1px solid #e7e7e7;
  max-width: none;
  padding: 0;
  margin-top: 7px;
  top: 100px;
  left: 20px;
  z-index: 3001;
  display: none;
  font-family: arial;
  font-size: 15px;
  line-height: 1em;
}

.daterangepicker:before,
.daterangepicker:after {
  display: none;
  position: absolute;
  display: inline-block;
  border-bottom-color: rgba(0, 0, 0, 0.2);
  content: "";
}

.daterangepicker:before {
  top: -7px;
  border-right: 7px solid transparent;
  border-left: 7px solid transparent;
  border-bottom: 7px solid #ccc;
}

.daterangepicker:after {
  top: -6px;
  border-right: 6px solid transparent;
  border-bottom: 6px solid #fff;
  border-left: 6px solid transparent;
}

.daterangepicker.opensleft:before {
  right: 9px;
}

.daterangepicker.opensleft:after {
  right: 10px;
}

.daterangepicker.openscenter:before {
  left: 0;
  right: 0;
  width: 0;
  margin-left: auto;
  margin-right: auto;
}

.daterangepicker.openscenter:after {
  left: 0;
  right: 0;
  width: 0;
  margin-left: auto;
  margin-right: auto;
}

.daterangepicker.opensright:before {
  left: 9px;
}

.daterangepicker.opensright:after {
  left: 10px;
}

.daterangepicker.drop-up {
  margin-top: -7px;
}

.daterangepicker.drop-up:before {
  top: initial;
  bottom: -7px;
  border-bottom: initial;
  border-top: 7px solid #ccc;
}

.daterangepicker.drop-up:after {
  top: initial;
  bottom: -6px;
  border-bottom: initial;
  border-top: 6px solid #fff;
}

.daterangepicker.single .daterangepicker .ranges,
.daterangepicker.single .drp-calendar {
  float: none;
}

.daterangepicker.single .drp-selected {
  display: none;
}

.daterangepicker.show-calendar .drp-calendar {
  display: block;
}

.daterangepicker.show-calendar .drp-buttons {
  display: block;
}

.daterangepicker.auto-apply .drp-buttons {
  display: none;
}

.daterangepicker .drp-calendar {
  display: none;
  max-width: 328px;
}

.daterangepicker .drp-calendar.left {
  padding: 8px 0 8px 8px;
}

.daterangepicker .drp-calendar.right {
  padding: 8px;
}

.daterangepicker .drp-calendar.single .calendar-table {
  border: none;
}

.daterangepicker .calendar-table .next span,
.daterangepicker .calendar-table .prev span {
  color: #fff;
  border: solid black;
  border-width: 0 2px 2px 0;
  border-radius: 0;
  display: inline-block;
  padding: 3px;
  margin-top: -12px;
  display: block;
  padding: 0;
  border: none;
  width: 30px;
  height: 30px;
  transform: none;
}

/* .daterangepicker .calendar-table .next span {
  transform: rotate(-45deg);
  -webkit-transform: rotate(-45deg);
}

.daterangepicker .calendar-table .prev span {
  transform: rotate(135deg);
  -webkit-transform: rotate(135deg);
} */
.daterangepicker .calendar-table .prev span {
  background: url(../../images/common/ico_calendar-prev.png) no-repeat center;
}
.daterangepicker .calendar-table .next span {
  background: url(../../images/common/ico_calendar-next.png) no-repeat center;
  text-align: right;
  display: inline-block;
  margin-right: 0;
  margin-left: 12px;
}
.daterangepicker .calendar-table th,
.daterangepicker .calendar-table td {
  white-space: nowrap;
  text-align: center;
  vertical-align: middle;
  min-width: 32px;
  width: 32px;
  height: 24px;
  line-height: 24px;
  font-size: 12px;
  border-radius: 4px;
  border: 1px solid transparent;
  white-space: nowrap;
  cursor: pointer;
  min-width: 42px;
  height: 42px;
  line-height: 22px;
  font-size: 15px;
  border: none;
}

.daterangepicker .calendar-table {
  border: 1px solid #fff;
  border-radius: 4px;
  background-color: #fff;
}

.daterangepicker .calendar-table table {
  width: 100%;
  margin: 0;
  border-spacing: 0;
  border-collapse: separate;
}

.daterangepicker td.available:hover {
  background-color: #e1e5ef;
  border-color: transparent;
  color: inherit;
}

.daterangepicker td.week,
.daterangepicker th.week {
  font-size: 80%;
  color: #ccc;
}

.daterangepicker td.off.today:after {
  display: none;
}
.daterangepicker td.in-range {
  background-color: #e1e5ef;
  border-color: transparent;
  color: #000;
  border-radius: 0;
}

.daterangepicker td.start-date {
  background: url(../../images/common/bg_calendar-start.png) no-repeat center;
}

.daterangepicker td.end-date {
  background: url(../../images/common/bg_calendar-end.png) no-repeat center;
}

.daterangepicker td.start-date.end-date {
  border-radius: 50%;
  font-weight: bold;
  background: #02164f;
}

.daterangepicker td.active,
.daterangepicker td.active:hover:not(.off:hover) {
  border-radius: 50%;
  background: #02164f;
  border-color: transparent;
  color: #fff;
}

.daterangepicker th.month {
  width: auto;
  padding-bottom: 10px;
  font-weight: bold;
  font-size: 16px;
  line-height: 23px;
  vertical-align: middle;
  text-align: center;
  color: #000000;
}

.daterangepicker td.disabled,
.daterangepicker option.disabled {
  color: #999;
  cursor: not-allowed;
  text-decoration: line-through;
}

.daterangepicker select.monthselect,
.daterangepicker select.yearselect {
  font-size: 12px;
  padding: 1px;
  height: auto;
  margin: 0;
  cursor: default;
}

.daterangepicker select.monthselect {
  margin-right: 2%;
  width: 56%;
}

.daterangepicker select.yearselect {
  width: 40%;
}

.daterangepicker select.hourselect,
.daterangepicker select.minuteselect,
.daterangepicker select.secondselect,
.daterangepicker select.ampmselect {
  width: 50px;
  margin: 0 auto;
  background: #eee;
  border: 1px solid #eee;
  padding: 2px;
  outline: 0;
  font-size: 12px;
}

.daterangepicker .calendar-time {
  text-align: center;
  margin: 4px auto 0 auto;
  line-height: 30px;
  position: relative;
}

.daterangepicker .calendar-time select.disabled {
  color: #ccc;
  cursor: not-allowed;
}

.daterangepicker .drp-buttons {
  clear: both;
  text-align: right;
  padding: 8px;
  border-top: 1px solid #ddd;
  display: none;
  line-height: 12px;
  vertical-align: middle;
  border-top: none;
  padding: 16px;
}

.daterangepicker .drp-selected {
  display: none;
  font-size: 12px;
  padding-right: 8px;
}

.daterangepicker .drp-buttons .btn {
  margin-left: 8px;
  font-size: 12px;
  font-weight: bold;
  padding: 4px 8px;
  padding: 10px 7px 9px;
  min-width: 84px;
  line-height: 19px;
  font-size: 13px;
}

.daterangepicker.show-ranges.single.rtl .drp-calendar.left {
  border-right: 1px solid #ddd;
}

.daterangepicker.show-ranges.single.ltr .drp-calendar.left {
  border-left: 1px solid #ddd;
}

.daterangepicker.show-ranges.rtl .drp-calendar.right {
  border-right: 1px solid #ddd;
}

.daterangepicker.show-ranges.ltr .drp-calendar.left {
  border-left: 1px solid #ddd;
}

.daterangepicker .ranges {
  float: none;
  text-align: left;
  margin: 0;
}

.daterangepicker.show-calendar .ranges {
  margin-top: 8px;
}

.daterangepicker .ranges ul {
  list-style: none;
  margin: 0 auto;
  padding: 0;
  width: 100%;
}

.daterangepicker .ranges li {
  font-size: 12px;
  padding: 8px 12px;
  cursor: pointer;
}

.daterangepicker .ranges li:hover {
  background-color: #eee;
}

.daterangepicker .ranges li.active {
  background-color: #08c;
  color: #fff;
}

/*  Larger Screen Styling */
@media (min-width: 564px) {
  .daterangepicker {
    width: auto;
  }

  .daterangepicker .ranges ul {
    width: 140px;
  }

  .daterangepicker.single .ranges ul {
    width: 100%;
  }

  .daterangepicker.single .drp-calendar.left {
    clear: none;
  }

  .daterangepicker.single .ranges,
  .daterangepicker.single .drp-calendar {
    float: left;
  }

  .daterangepicker {
    direction: ltr;
    text-align: left;
  }

  .daterangepicker .drp-calendar.left {
    clear: left;
    margin-right: 0;
  }

  .daterangepicker .drp-calendar.left .calendar-table {
    border-right: none;
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
  }

  .daterangepicker .drp-calendar.right {
    margin-left: 0;
  }

  .daterangepicker .drp-calendar.right .calendar-table {
    border-left: none;
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
  }

  .daterangepicker .drp-calendar.left .calendar-table {
    padding-right: 0px;
  }

  .daterangepicker .ranges,
  .daterangepicker .drp-calendar {
    float: left;
  }
}
.daterangepicker td.off {
  background: none !important;
}

.daterangepicker td.off,
.daterangepicker td.off.in-range,
.daterangepicker td.off.start-date,
.daterangepicker td.off.ends.start-date,
.daterangepicker td.off.end-date,
.daterangepicker td.today.off,
.daterangepicker td.today.active.off,
.daterangepicker td.off.active.ends.start-date:hover,
.daterangepicker td.off.active.ends.end-date:hover {
  background-color: #fff;
  border-color: transparent;
  color: #999;
  border: none;
  border-color: transparent;
  font-weight: 400;
  background: none;
  color: #41495e;
}
.daterangepicker td.active.start-date.end-date.available:hover {
  border-radius: 50%;
}

@media (min-width: 730px) {
  .daterangepicker .ranges {
    width: auto;
  }

  .daterangepicker .ranges {
    float: left;
  }

  .daterangepicker.rtl .ranges {
    float: right;
  }

  .daterangepicker .drp-calendar.left {
    clear: none !important;
  }
}

.daterangepicker .calendar-table table thead tr:nth-child(2) {
  display: none;
}
.daterangepicker .drp-calendar.left,
.daterangepicker .drp-calendar.right {
  padding: 20px 16px 0;
  box-sizing: border-box;
}
.daterangepicker td {
  border-radius: 50%;
}
.daterangepicker td.start-date,
.daterangepicker td.active.start-date:hover,
.daterangepicker td.active.start-date.available:hover,
.daterangepicker td.start-date:hover {
  font-weight: bold;
  border-radius: 0;
  background: url(../../images/common/bg_calendar-start.png) no-repeat right
    center/42px auto;
}
.daterangepicker td.end-date,
.daterangepicker td.active.end-date:hover,
.daterangepicker td.active.end-date.available:hover,
.daterangepicker td.end-date:hover {
  font-weight: bold;
  border-radius: 0;
  background: url(../../images/common/bg_calendar-end.png) no-repeat left
    center/42px auto;
}

.daterangepicker td.today {
  position: relative;
  font-weight: bold;
  color: #02164f;
}
.daterangepicker td.today:after {
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  width: 97%;
  height: 97%;
  background-color: transparent;
  border: 1px solid #02164f;
  border-radius: 50%;
}
.daterangepicker td.today.active {
  color: #fff;
}
.daterangepicker td.today.active:after {
  display: none;
}
