/* .daterangepicker td.available:hover::after {position: absolute; top:0; left:0; width:100%; height:100%; background-color: red; z-index: -1; content: '';} */

.daterangepicker .calendar-table th.prev,
.daterangepicker .calendar-table th.next,
.daterangepicker .calendar-table td {
  width: 42px;
}
.daterangepicker .calendar-table thead th:first-child:not(.available),
.daterangepicker .calendar-table thead th.month + th:not(.available) {
  width: 42px;
}
/* .daterangepicker .calendar-table.prev:before,
.daterangepicker .calendar-table.next:after {position:absolute; top:0; left:0; border-radius:50%; content:''} */
/* .daterangepicker td.start-date:before {display:inline-block; position:absolute;
  top:0; right:0px; width:10px; height:29px; background-color: #E1E5EF;z-index: 1; content:''} */
.daterangepicker td.start-date,
.daterangepicker td.end-date,
.daterangepicker td.start-date.end-date {
  position: relative;
  border-color: transparent;
}

.daterangepicker .drp-buttons .btn-reset {
  background: url(../../images/common/ico_refresh.png) no-repeat left 14px
    center;
  padding-left: 34px;
}
.daterangepicker .drp-buttons .btn-reset.hide {
  display: none;
}
.daterangepicker .drp-buttons .btn-reset:hover {
  background-color: #e6e8ed;
}
